mapper.delete:
UPDATE comment
SET `status` = 0,
updated_user = #{updatedUser, jdbcType=VARCHAR}
WHERE id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT id, postId, content, created_user, updated_user, db_create_time, db_modify_time

FROM comment
WHERE 1=1

    AND postId = #{postId}


    AND created_user = #{userId}


    AND content LIKE CONCAT('%', #{keyword}, '%')

SELECT EXISTS(
SELECT id
FROM comment
WHERE id = #{id,jdbcType=BIGINT}
)
