mapper.insert:
INSERT INTO follow(followerId, followingId, status)
VALUES (
#{followerId, jdbcType=VARCHAR},
#{followingId, jdbcType=VARCHAR},
#{status,jdbcType=TINYINT}
)

mapper.update:
UPDATE follow
SET status = #{status, jdbcType=TINYINT}
WHERE 1=1

    AND followerId = #{followerId, jdbcType=VARCHAR}


    AND followingId = #{followingId, jdbcType=VARCHAR}

mapper.select:
SELECT EXISTS(
SELECT id
FROM follow
WHERE followerId = #{followerId, jdbcType=VARCHAR}
AND followingId = #{followingId, jdbcType=VARCHAR}
)

SELECT EXISTS(
SELECT id
FROM follow
WHERE followerId = #{followerId, jdbcType=VARCHAR}
AND followingId = #{followingId, jdbcType=VARCHAR}
AND status = #{status, jdbcType=TINYINT}
)
