mapper.update:
UPDATE post

    updated_user = #{updatedUser, jdbcType=VARCHAR},

        content = #{content, jdbcType=VARCHAR},


        referenceId = #{referenceId, jdbcType=BIGINT},


        status = #{status,jdbcType=TINYINT}


WHERE id = #{id, jdbcType=BIGINT}

mapper.delete:
UPDATE post
SET `status` = 0,
updated_user = #{updatedUser, jdbcType=VARCHAR}
WHERE id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT id, content, referenceId, created_user, updated_user, db_create_time, db_modify_time

FROM post
WHERE 1=1

    AND id = #{postId}


    AND created_user = #{userId}


    AND content LIKE CONCAT('%', #{keyword}, '%')

SELECT EXISTS(
SELECT id
FROM post
WHERE id = #{id,jdbcType=BIGINT} AND created_user = #{userId, jdbcType=VARCHAR}
)
