mapper.update:
UPDATE star


        isActive = #{isActive, jdbcType=BOOLEAN}


WHERE targetType = #{targetType, jdbcType=TINYINT}
AND targetId = #{targetId, jdbcType=BIGINT}
AND userId = #{userId, jdbcType=VARCHAR}

mapper.select:
SELECT EXISTS(
SELECT id
FROM star
WHERE targetType = #{targetType, jdbcType=TINYINT}
AND targetId = #{targetId, jdbcType=BIGINT}
AND userId = #{userId, jdbcType=VARCHAR}
)

SELECT EXISTS(
SELECT id
FROM star
WHERE targetType = #{targetType, jdbcType=TINYINT}
AND targetId = #{targetId, jdbcType=BIGINT}
AND isActive = #{isActive, jdbcType=BOOLEAN}
AND userId = #{userId, jdbcType=VARCHAR}
)

SELECT COUNT(id)
FROM Star
WHERE targetType = #{targetType, jdbcType=TINYINT}
AND targetId = #{targetId, jdbcType=BIGINT}
AND isActive = TRUE

SELECT targetId

FROM Star
WHERE targetType = #{targetType, jdbcType=TINYINT}
AND userId = #{userId, jdbcType=VARCHAR}
AND isActive = TRUE
