mapper.update:
UPDATE star


        isActive = #{isActive, jdbcType=BOOLEAN}


WHERE targetId = #{targetId, jdbcType=BIGINT}
AND userId = #{userId, jdbcType=VARCHAR}
AND targetType = #{targetType, jdbcType=TINYINT}

mapper.select:
SELECT EXISTS(
SELECT id
FROM star
WHERE targetId = #{targetId, jdbcType=BIGINT}
AND userId = #{userId, jdbcType=VARCHAR}
AND targetType = #{targetType, jdbcType=TINYINT}
)

SELECT EXISTS(
SELECT id
FROM star
WHERE targetId = #{targetId, jdbcType=BIGINT}
AND userId = #{userId, jdbcType=VARCHAR}
AND targetType = #{targetType, jdbcType=TINYINT}
AND isActive = #{isActive, jdbcType=BOOLEAN}
)

SELECT COUNT(id)
FROM Star
WHERE targetId = #{targetId, jdbcType=BIGINT}
AND targetType = #{targetType, jdbcType=TINYINT}
AND isActive = TRUE
